import os
import json
import matplotlib.pyplot as plt
directory_path = '../fv_som_results'

fv_qe = {}
fv_wd = {}
fv_e = {}

for file in os.listdir(directory_path):
    size = int(file.split("n")[0])
    with open(directory_path+"/"+file, 'r') as r:
        data = json.load(r)
        fv_qe[size] = data["quant_err"]
        fv_wd[size] = [100*i for i in data["wd"]]
        fv_e[size] = data["entropy"]


# print(plt.style.available)
plt.style.use('bmh')

# Create figure and subplots
fig, axs = plt.subplots(1, 3, figsize=(15, 5))

# Subplot 1
for i in [6, 7, 8, 9, 10, 15]:
    axs[0].plot(fv_qe[i])
axs[0].set_title('Quantization error')

# Subplot 2
for i in [6, 7, 8, 9, 10, 15]:
    axs[1].plot(fv_wd[i])
axs[1].set_title('Winner discrimination (%)')

# Subplot 3
for i in [6, 7, 8, 9, 10, 15]:
    axs[2].plot(fv_e[i], label=f"{i} x {i}")
axs[2].set_title('Entropy')

fig.legend(loc="lower left", bbox_to_anchor=(0.05, 0.09),
           fancybox=True, title="SOM size", shadow=True)

# Adjust layout
plt.tight_layout()

# Save plot as image
plt.savefig('fv-som-measures.png')



# fill table
epoch = 85 - 1
for i in [6, 7, 8, 9, 10, 15]:
	print(f'{i} ' + '$\\times$ ' +  f'{i}' +  '& \multicolumn{1}{r|}{' + str(round(fv_qe[i][epoch], 2)) + '}   & \multicolumn{1}{r|}{' + str(round(fv_wd[i][epoch], 0)) + '\\%}   &  ' + str(round(fv_e[i][epoch], 2)) + ' & \multicolumn{1}{r|}{' + str(round(cifar_qe[i][epoch], 2)) + '} & \multicolumn{1}{r|}{' + str(round(cifar_wd[i][epoch], 0)) + '\\%} &  ' +    str(round(cifar_e[i][epoch], 2))   +         ' \\\ \hline')
